          .md-footer
            b-field
              b-checkbox(type="is-info" v-model="acceptTerm") I accept this terms and conditions.
            b-field.has-text-centered(v-if="acceptTerm")
              button.button.btn-des-1(@click.prevent="acceptSbt") Accept
        b-loading(:is-full-page="false" :active="loading" :can-cancel="false")

</template>

<script>
export default {
  props: {
    isSignup: {
      type: Boolean,
      default: false
    },
    md_active: {
      type: Boolean,
      default: false
    }
  },
  mounted() {
    const self = this;
    if (self.isSignup === false) {
      if (self.$store.state.hasTermsLoaded === false) {
        this.$axios
          .get("/api/member/get_terms_sts")
          .then(res => {
            if (res.data.accept_sts === 0) {
              self.modalAct = true;
            } else {
              self.$store.commit("hasTermsLoadedSet", true);
            }
          })
          .catch(err => {
            console.log(err);
          });
      }
    } else {
      self.modalAct = self.md_active;
    }
  },
  watch: {
    md_active(val) {
      this.modalAct = val;
    }
  },
  data() {
    return {
      loading: false,
      modalAct: false,
      acceptTerm: false
    };
  },
  methods: {
    async acceptSbt() {
      const self = this;
      if (self.isSignup === false) {
        self.loading = true;
        await this.$axios
          .post("/api/member/terms_accept")
          .then(res => {
            self.loading = false;
            self.modalAct = false;
            self.$store.commit("hasTermsLoadedSet", true);
          })
          .catch(err => {
            self.loading = false;
            console.log(err);
          });
      } else {
        self.$emit("accept_terms_tr", true);
      }
    }
  }
};
</script>

